create table blocks
(
    numID           varchar(5)   null,
    name            varchar(25)  null,
    textID          varchar(32)  null,
    stackSize       int          null,
    craftability    tinyint(1)   null,
    tool            varchar(3)   null,
    hardness        float        null,
    blastResistance float        null,
    transparent     tinyint(1)   null,
    flammability    tinyint(1)   null,
    fuel            tinyint(1)   null,
    renewability    tinyint(1)   null,
    description     varchar(128) null,
    rarity          varchar(2)   null,
    constraint blocks_ibfk_1
        foreign key (tool) references blocktools (toolID),
    constraint blocks_ibfk_2
        foreign key (rarity) references rarity (rarityID)
);

create index rarity
    on blocks (rarity);

create index tool
    on blocks (tool);

