create table blocks
(
    numID           varchar(5)   not null
        primary key,
    name            varchar(32)  null,
    textID          varchar(64)  null,
    stackSize       int          null,
    craftability    tinyint(1)   null,
    hardness        float        null,
    blastResistance float        null,
    transparency    tinyint(1)   null,
    flammability    tinyint(1)   null,
    fuel            tinyint(1)   null,
    renewability    tinyint(1)   null,
    description     varchar(128) null,
    rarityID        varchar(2)   null,
    constraint blocks_ibfk_2
        foreign key (rarityID) references rarity (rarityID)
);

create index rarity
    on blocks (rarityID);

